#TODO Expand so we keep words which look like they are on sentence boundaries
class StopWords():
    '''Build a list of stopwords and add a whitelist
    then use purge to purge you string of stopwords not
    in whitelist
    
    whitelist: set of words to keep
    stopwords: list of additional stopwords to use, added to those in DefaultStopwords
    '''

    def __init__(self, whitelist=None, stopwords=None):
        #build whitelist
        if not whitelist: whitelist = set()
        assert isinstance(whitelist, set), 'Unexepected type for whitelist, expected set or None'
        self.whitelist = whitelist
        self.whitelist |= DefaultWhitelists.Numericals

        #build stopwords
        if not stopwords: stopwords = set()
        assert isinstance(stopwords, set), 'Unexepected type for stopwords, expected set or None'
        self.stopwords = stopwords        
        self.stopwords |= DefaultStopwords.All

        #stopwords now contains nothing in whitelist
        self.stopwords = self.stopwords.difference(self.whitelist)
        self.purged = ''
    

        


    def purge(self, s):
        '''(str) -> str
        purge string s of stopwords

        Example ("the" is a stop word):
        >>>StopWords.purge_iter('the quick brown')
        'quick brown'
        '''
        #we cant use sets - sets dont retain order after operations
        s = s.lower()
        word_tokens = [w for w in _word_tokenize(s) if not w in self.stopwords]
        out = _Detok().detokenize(list(word_tokens))
        return out
        


    def purge_iter(self, itr):
        '''(iter) -> iter
        as purge, but purge an iterable

        Example (assuming "the" is a stop word):
        >>>StopWords.purge_iter(['the quick brown', 'fox jumped'])
        ['quick brown', 'fox jumped']
        '''
        return [self.purge(it) for it in itr]